mapper.select:
SELECT *
  FROM diary
 WHERE user_id = #{userId} AND diary_date = #{diaryDate}

mapper.delete:
DELETE FROM diary
WHERE user_id = #{userId}
AND diary_date = #{diaryDate}

mapper.update:
UPDATE diary
   SET user_id = #{ userId }, title = #{ title }, 
   				 content = #{ content }, diary_date = #{ diaryDate },
   				 diary_picture = #{ diaryPicture },	location = #{ location }
 WHERE user_id = #{userId} AND diary_date = #{diaryDate}
